import React from 'react';
import Tooltip from './Tooltip';

const statusDescriptions = {
  '–ù–æ–≤—ã–π': '–ó–∞–∫–∞–∑ —Ç–æ–ª—å–∫–æ —á—Ç–æ —Å–æ–∑–¥–∞–Ω –∏ –æ–∂–∏–¥–∞–µ—Ç –æ–±—Ä–∞–±–æ—Ç–∫–∏',
  '–°–æ–±–∏—Ä–∞–µ—Ç—Å—è': '–ó–∞–∫–∞–∑ –≤ –ø—Ä–æ—Ü–µ—Å—Å–µ –∫–æ–º–ø–ª–µ–∫—Ç–∞—Ü–∏–∏',
  '–î–æ—Å—Ç–∞–≤–ª–µ–Ω–æ': '–ó–∞–∫–∞–∑ —É—Å–ø–µ—à–Ω–æ –¥–æ—Å—Ç–∞–≤–ª–µ–Ω –∫–ª–∏–µ–Ω—Ç—É',
};

const StatusBadge = ({ status }) => {
  const getStatusConfig = (status) => {
    switch (status) {
      case '–ù–æ–≤—ã–π':
        return {
          className: 'status-badge status-new',
          icon: 'üÜï'
        };
      case '–°–æ–±–∏—Ä–∞–µ—Ç—Å—è':
        return {
          className: 'status-badge status-processing',
          icon: 'üì¶'
        };
      case '–î–æ—Å—Ç–∞–≤–ª–µ–Ω–æ':
        return {
          className: 'status-badge status-delivered',
          icon: '‚úÖ'
        };
      default:
        return {
          className: 'status-badge bg-gray-100 text-gray-700',
          icon: '‚ùì'
        };
    }
  };

  const config = getStatusConfig(status);

  return (
    <Tooltip content={statusDescriptions[status] || status}>
      <span className={config.className}>
        <span className="mr-1">{config.icon}</span>
        {status}
      </span>
    </Tooltip>
  );
};

export default StatusBadge;
