import React, { useState } from 'react';
import { 
  ChevronUp, 
  ChevronDown, 
  CheckSquare, 
  Square,
  Download,
  Trash2,
  Edit,
  MoreHorizontal
} from 'lucide-react';
import CustomerCard from './CustomerCard';

const DataTable = ({ 
  customers, 
  onStatusChange, 
  onDelete, 
  onEdit,
  selectedItems = [],
  onSelectionChange,
  sortConfig = null,
  onSort
}) => {
  const [showActions, setShowActions] = useState(false);

  const handleSelectAll = () => {
    if (selectedItems.length === customers.length) {
      onSelectionChange([]);
    } else {
      onSelectionChange(customers.map(c => c.id));
    }
  };

  const handleSelectItem = (id) => {
    if (selectedItems.includes(id)) {
      onSelectionChange(selectedItems.filter(itemId => itemId !== id));
    } else {
      onSelectionChange([...selectedItems, id]);
    }
  };

  const getSortIcon = (field) => {
    if (sortConfig?.key !== field) return null;
    return sortConfig.direction === 'asc' ? 
      <ChevronUp className="w-4 h-4" /> : 
      <ChevronDown className="w-4 h-4" />;
  };

  const handleSort = (field) => {
    const direction = sortConfig?.key === field && sortConfig.direction === 'asc' ? 'desc' : 'asc';
    onSort({ key: field, direction });
  };

  return (
    <div className="space-y-4">
      {/* Header с массовыми операциями */}
      {selectedItems.length > 0 && (
        <div className="bento-card p-4 bg-blue-50 border-blue-200">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <span className="text-sm font-medium text-blue-700">
                Выбрано {selectedItems.length} клиентов
              </span>
              <button
                onClick={() => onSelectionChange([])}
                className="text-sm text-blue-600 hover:text-blue-800"
              >
                Отменить выбор
              </button>
            </div>

            <div className="flex items-center space-x-2">
              <button className="btn-secondary flex items-center space-x-2 text-sm">
                <Download className="w-4 h-4" />
                <span>Экспорт</span>
              </button>
              <button className="btn-secondary flex items-center space-x-2 text-sm text-red-600 hover:text-red-700">
                <Trash2 className="w-4 h-4" />
                <span>Удалить</span>
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Таблица */}
      <div className="bento-card overflow-hidden">
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left">
                  <button
                    onClick={handleSelectAll}
                    className="flex items-center space-x-2 hover:text-gray-700"
                  >
                      {selectedItems.length === customers.length ? 
                        <CheckSquare className="w-4 h-4" /> : 
                        <Square className="w-4 h-4" />
                      }
                    </button>
                </th>
                <th className="px-6 py-3 text-left">
                  <button
                    onClick={() => handleSort('name')}
                    className="flex items-center space-x-2 hover:text-gray-700 font-medium"
                  >
                    <span>Имя клиента</span>
                    {getSortIcon('name')}
                  </button>
                </th>
                <th className="px-6 py-3 text-left">
                  <button
                    onClick={() => handleSort('phone')}
                    className="flex items-center space-x-2 hover:text-gray-700 font-medium"
                  >
                    <span>Телефон</span>
                    {getSortIcon('phone')}
                  </button>
                </th>
                <th className="px-6 py-3 text-left">
                  <button
                    onClick={() => handleSort('status')}
                    className="flex items-center space-x-2 hover:text-gray-700 font-medium"
                  >
                    <span>Статус</span>
                    {getSortIcon('status')}
                  </button>
                </th>
                <th className="px-6 py-3 text-left">
                  <button
                    onClick={() => handleSort('createdAt')}
                    className="flex items-center space-x-2 hover:text-gray-700 font-medium"
                  >
                    <span>Дата создания</span>
                    {getSortIcon('createdAt')}
                  </button>
                </th>
                <th className="px-6 py-3 text-left font-medium">Действия</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-gray-200">
              {customers.map((customer) => (
                <tr key={customer.id} className="hover:bg-gray-50">
                  <td className="px-6 py-4">
                    <button
                      onClick={() => handleSelectItem(customer.id)}
                      className="flex items-center space-x-2"
                    >
                      {selectedItems.includes(customer.id) ? 
                        <CheckSquare className="w-4 h-4 text-primary-600" /> : 
                        <Square className="w-4 h-4" />
                      }
                    </button>
                  </td>
                  <td className="px-6 py-4">
                    <div>
                      <div className="font-medium text-gray-900">{customer.name}</div>
                      <div className="text-sm text-gray-500">{customer.order}</div>
                    </div>
                  </td>
                  <td className="px-6 py-4 text-sm text-gray-900">
                    {customer.phone}
                  </td>
                  <td className="px-6 py-4">
                    <select
                      value={customer.status}
                      onChange={(e) => onStatusChange(customer.id, e.target.value)}
                      className="text-xs border border-gray-200 rounded-lg px-2 py-1 focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                    >
                      <option value="Новый">Новый</option>
                      <option value="Собирается">Собирается</option>
                      <option value="Доставлено">Доставлено</option>
                    </select>
                  </td>
                  <td className="px-6 py-4 text-sm text-gray-500">
                    {new Date(customer.createdAt).toLocaleDateString('ru-RU')}
                  </td>
                  <td className="px-6 py-4">
                    <div className="flex items-center space-x-2">
                      <button
                        onClick={() => onEdit(customer)}
                        className="p-1 text-gray-400 hover:text-gray-600"
                        title="Редактировать"
                      >
                        <Edit className="w-4 h-4" />
                      </button>
                      <button
                        onClick={() => onDelete(customer.id)}
                        className="p-1 text-gray-400 hover:text-red-600"
                        title="Удалить"
                      >
                        <Trash2 className="w-4 h-4" />
                      </button>
                      <button
                        onClick={() => setShowActions(!showActions)}
                        className="p-1 text-gray-400 hover:text-gray-600"
                        title="Дополнительные действия"
                      >
                        <MoreHorizontal className="w-4 h-4" />
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
};

export default DataTable;
