import React, { useState, useMemo } from 'react';
import { Plus, Users, Package, Truck, CheckCircle } from 'lucide-react';
import { useLocalStorage } from './hooks/useLocalStorage';
import CustomerCard from './components/CustomerCard';
import AddCustomerModal from './components/AddCustomerModal';
import StatsCard from './components/StatsCard';

// Начальные данные для демонстрации
const initialCustomers = [
  {
    id: '1',
    name: 'Анна Петрова',
    phone: '+375 29 123-45-67',
    order: 'Органические овощи: морковь 2кг, картофель 5кг, лук 1кг',
    status: 'Новый'
  },
  {
    id: '2',
    name: 'Иван Сидоров',
    phone: '+375 33 987-65-43',
    order: 'Фермерские яйца 30шт, молоко 2л, творог 500г',
    status: 'Собирается'
  },
  {
    id: '3',
    name: 'Мария Козлова',
    phone: '+375 25 555-12-34',
    order: 'Мед липовый 1кг, хлеб домашний 2шт, масло сливочное 200г',
    status: 'Доставлено'
  }
];

function App() {
  const [customers, setCustomers] = useLocalStorage('customers', initialCustomers);
  const [filterStatus, setFilterStatus] = useState('Все');
  const [isModalOpen, setIsModalOpen] = useState(false);

  const filteredCustomers = useMemo(() => {
    if (filterStatus === 'Все') {
      return customers;
    }
    return customers.filter(customer => customer.status === filterStatus);
  }, [customers, filterStatus]);

  const stats = useMemo(() => {
    const total = customers.length;
    const newOrders = customers.filter(c => c.status === 'Новый').length;
    const processing = customers.filter(c => c.status === 'Собирается').length;
    const delivered = customers.filter(c => c.status === 'Доставлено').length;

    return { total, newOrders, processing, delivered };
  }, [customers]);

  const handleAddCustomer = (newCustomer) => {
    setCustomers([...customers, newCustomer]);
  };

  const handleStatusChange = (customerId, newStatus) => {
    setCustomers(customers.map(customer =>
      customer.id === customerId
        ? { ...customer, status: newStatus }
        : customer
    ));
  };

  const handleDeleteCustomer = (customerId) => {
    if (window.confirm('Вы уверены, что хотите удалить этого клиента?')) {
      setCustomers(customers.filter(customer => customer.id !== customerId));
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center space-x-3">
              <div className="w-10 h-10 bg-primary-600 rounded-xl flex items-center justify-center">
                <Package className="w-6 h-6 text-white" />
              </div>
              <div>
                <h1 className="text-2xl font-bold text-gray-900">Dostavim.by</h1>
                <p className="text-sm text-gray-500">CRM система доставки</p>
              </div>
            </div>
            <button
              onClick={() => setIsModalOpen(true)}
              className="btn-primary flex items-center space-x-2"
            >
              <Plus className="w-5 h-5" />
              <span>Добавить клиента</span>
            </button>
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Статистика */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          <StatsCard
            title="Всего клиентов"
            value={stats.total}
            icon={<Users className="w-6 h-6" />}
            color="primary"
          />
          <StatsCard
            title="Новые заказы"
            value={stats.newOrders}
            icon={<Package className="w-6 h-6" />}
            color="primary"
          />
          <StatsCard
            title="В обработке"
            value={stats.processing}
            icon={<Truck className="w-6 h-6" />}
            color="warning"
          />
          <StatsCard
            title="Доставлено"
            value={stats.delivered}
            icon={<CheckCircle className="w-6 h-6" />}
            color="success"
          />
        </div>

        {/* Фильтры */}
        <div className="bento-card p-6 mb-8">
          <div className="flex items-center justify-between">
            <h2 className="text-xl font-semibold text-gray-900">Клиенты</h2>
            <div className="flex items-center space-x-4">
              <label className="text-sm font-medium text-gray-700">Фильтр по статусу:</label>
              <select
                value={filterStatus}
                onChange={(e) => setFilterStatus(e.target.value)}
                className="border border-gray-200 rounded-lg px-3 py-2 text-sm focus:ring-2 focus:ring-primary-500 focus:border-transparent"
              >
                <option value="Все">Все</option>
                <option value="Новый">Новые</option>
                <option value="Собирается">В обработке</option>
                <option value="Доставлено">Доставленные</option>
              </select>
            </div>
          </div>
        </div>

        {/* Список клиентов */}
        {filteredCustomers.length === 0 ? (
          <div className="bento-card p-12 text-center">
            <Package className="w-16 h-16 text-gray-300 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">
              {filterStatus === 'Все' ? 'Нет клиентов' : `Нет клиентов со статусом "${filterStatus}"`}
            </h3>
            <p className="text-gray-500">
              {filterStatus === 'Все' 
                ? 'Добавьте первого клиента, нажав кнопку "Добавить клиента"'
                : 'Попробуйте изменить фильтр или добавьте нового клиента'
              }
            </p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {filteredCustomers.map(customer => (
              <CustomerCard
                key={customer.id}
                customer={customer}
                onStatusChange={handleStatusChange}
              />
            ))}
          </div>
        )}
      </div>

      {/* Модальное окно добавления клиента */}
      <AddCustomerModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onAdd={handleAddCustomer}
      />
    </div>
  );
}

export default App;
