import React from 'react';
import { Phone, User, Package } from 'lucide-react';
import StatusBadge from './StatusBadge';

const CustomerCard = ({ customer, onStatusChange }) => {
  const handleStatusChange = (e) => {
    onStatusChange(customer.id, e.target.value);
  };

  return (
    <div className="bento-card p-6 hover:scale-[1.02] transition-transform duration-300">
      <div className="flex items-start justify-between mb-4">
        <div className="flex items-center space-x-3">
          <div className="w-12 h-12 bg-primary-100 rounded-xl flex items-center justify-center">
            <User className="w-6 h-6 text-primary-600" />
          </div>
          <div>
            <h3 className="font-semibold text-gray-900 text-lg">{customer.name}</h3>
            <div className="flex items-center space-x-2 text-gray-500 text-sm">
              <Phone className="w-4 h-4" />
              <span>{customer.phone}</span>
            </div>
          </div>
        </div>
        <StatusBadge status={customer.status} />
      </div>

      <div className="space-y-3">
        <div className="flex items-center space-x-2 text-gray-600">
          <Package className="w-4 h-4" />
          <span className="text-sm">{customer.order}</span>
        </div>

        <div className="flex items-center space-x-2">
          <label className="text-xs font-medium text-gray-500">Статус:</label>
          <select
            value={customer.status}
            onChange={handleStatusChange}
            className="text-xs border border-gray-200 rounded-lg px-2 py-1 focus:ring-2 focus:ring-primary-500 focus:border-transparent"
          >
            <option value="Новый">Новый</option>
            <option value="Собирается">Собирается</option>
            <option value="Доставлено">Доставлено</option>
          </select>
        </div>
      </div>
    </div>
  );
};

export default CustomerCard;
